// pages/AdminDashboard.jsx
import { useState } from 'react';
import { Link } from 'react-router-dom';
import CampaignForm from '../components/Admin/CampaignForm';
import CampaignList from '../components/Admin/CampaignList';

// Temporary data (replace with API calls)
const initialCampaigns = [
  {
    id: '1',
    name: 'Summer Sale',
    description: 'Promote summer product collection',
    status: 'active',
    trackingLink: 'https://link.hiqmobi.com/summer-sale'
  }
];

export default function AdminDashboard() {
  const [campaigns, setCampaigns] = useState(initialCampaigns);
  const [selectedCampaign, setSelectedCampaign] = useState(null);

  const handleSubmit = (data) => {
    if (selectedCampaign) {
      setCampaigns(campaigns.map(c => 
        c.id === selectedCampaign.id ? { ...c, ...data } : c
      ));
    } else {
      setCampaigns([...campaigns, { ...data, id: Date.now().toString() }]);
    }
    setSelectedCampaign(null);
  };

  const handleDelete = (id) => {
    setCampaigns(campaigns.filter(c => c.id !== id));
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Admin Dashboard</h1>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div>
          <h2 className="text-xl font-semibold mb-4">Manage Campaigns</h2>
          <CampaignList 
            campaigns={campaigns}
            onDelete={handleDelete}
          />
          <Link
            to="/admin/campaigns/new"
            className="mt-4 inline-block bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Create New Campaign
          </Link>
        </div>

        <div>
          <h2 className="text-xl font-semibold mb-4">
            {selectedCampaign ? 'Edit Campaign' : 'Create New Campaign'}
          </h2>
          <CampaignForm 
            onSubmit={handleSubmit}
            initialData={selectedCampaign}
          />
        </div>
      </div>
    </div>
  );
}
